import React, { useState, useEffect } from 'react';
import '../styles/UserTable.css';

const UserTable = ({ users, onRowClick, onSort, sortConfig }) => {
  const [columnWidths, setColumnWidths] = useState({});

  const startResizing = (index) => (e) => {
    document.addEventListener('mousemove', resizeColumn(index));
    document.addEventListener('mouseup', stopResizing);
  };

  const resizeColumn = (index) => (e) => {
    const newColumnWidths = { ...columnWidths, [index]: e.clientX };
    setColumnWidths(newColumnWidths);
  };

  const stopResizing = () => {
    document.removeEventListener('mousemove', resizeColumn);
    document.removeEventListener('mouseup', stopResizing);
  };

  const getSortIndicator = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? '↑' : '↓';
    }
    return '↕';
  };

  useEffect(() => {
    const initialWidths = {};
    document.querySelectorAll('.table th').forEach((th, index) => {
      initialWidths[index] = th.offsetWidth;
    });
    setColumnWidths(initialWidths);
  }, []);

  return (
    <table className='table'>
      <thead>
        <tr>
          <th style={{ width: columnWidths[0] }} onClick={() => onSort('fullName')}>
            ФИО {getSortIndicator('fullName')}
            <div className='resizer' onMouseDown={startResizing(0)} />
          </th>
          <th style={{ width: columnWidths[1] }} onClick={() => onSort('age')}>
            Возраст {getSortIndicator('age')}
            <div className='resizer' onMouseDown={startResizing(1)} />
          </th>
          <th style={{ width: columnWidths[2] }} onClick={() => onSort('gender')}>
            Пол {getSortIndicator('gender')}
            <div className='resizer' onMouseDown={startResizing(2)} />
          </th>
          <th style={{ width: columnWidths[3] }}>
            Номер телефона
            <div className='resizer' onMouseDown={startResizing(3)} />
          </th>
          <th style={{ width: columnWidths[4] }} onClick={() => onSort('address')}>
            Адрес {getSortIndicator('address')}
            <div className='resizer' onMouseDown={startResizing(4)} />
          </th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr key={user.id} onClick={() => onRowClick(user)}>
            <td>{user.firstName} {user.lastName}</td>
            <td>{user.age}</td>
            <td>{user.gender}</td>
            <td>{user.phone}</td>
            <td>{user.address.city}, {user.address.address}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
