import React, { useState, useEffect } from 'react';
import UserTable from './components/UserTable';
import SearchBar from './components/SearchBar';
import UserModal from './components/UserModal';

const App = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [error, setError] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: null });

  // Fetch initial user data
  useEffect(() => {
    fetch('https://dummyjson.com/users')
      .then(response => response.json())
      .then(data => {
        if (data && data.users) {
          setUsers(data.users);
          setFilteredUsers(data.users);
        } else {
          setUsers([]);
          setFilteredUsers([]);
        }
      })
      .catch(error => setError(error));
  }, []);

  // Handle search query
  const handleSearch = query => {
    const lowerCaseQuery = query.toLowerCase();
    const result = users.filter(user => {
      const fullName = `${user.firstName} ${user.lastName}`.toLowerCase();
      const age = user.age.toString();
      const gender = user.gender ? user.gender.toLowerCase() : '';
      const phone = user.phone || '';
      const city = user.address?.city ? user.address.city.toLowerCase() : '';
      const street = user.address?.street ? user.address.street.toLowerCase() : '';

      return (
        fullName.includes(lowerCaseQuery) ||
        age.includes(lowerCaseQuery) ||
        gender.includes(lowerCaseQuery) ||
        phone.includes(lowerCaseQuery) ||
        city.includes(lowerCaseQuery) ||
        street.includes(lowerCaseQuery)
      );
    });
    setFilteredUsers(result);
  };

  // Handle sorting logic
  const handleSort = key => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    } else if (sortConfig.key === key && sortConfig.direction === 'descending') {
      direction = null;
      key = null;
    }
    setSortConfig({ key, direction });
  };

  // Sort users based on the selected key and direction
  const sortedUsers = React.useMemo(() => {
    let sortableUsers = [...filteredUsers];
    if (sortConfig.key !== null) {
      sortableUsers.sort((a, b) => {
        const aValue = sortConfig.key === 'fullName' ? `${a.firstName} ${a.lastName}` : sortConfig.key === 'address' ? `${a.address.city}, ${a.address.street}` : a[sortConfig.key];
        const bValue = sortConfig.key === 'fullName' ? `${b.firstName} ${b.lastName}` : sortConfig.key === 'address' ? `${b.address.city}, ${b.address.street}` : b[sortConfig.key];

        if (aValue < bValue) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableUsers;
  }, [filteredUsers, sortConfig]);

  // Handle row click to show modal
  const handleRowClick = user => {
    setSelectedUser(user);
  };

  // Close modal
  const closeModal = () => {
    setSelectedUser(null);
  };

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div style={{ width: '100%', maxWidth: '1200px', margin: '0 auto' }}>
      <SearchBar onSearch={handleSearch} />
      <UserTable users={sortedUsers} onRowClick={handleRowClick} onSort={handleSort} sortConfig={sortConfig} />
      {selectedUser && <UserModal user={selectedUser} onClose={closeModal} />}
    </div>
  );
};

export default App;
