import React, { useRef, useEffect, useState } from 'react';
import '../styles/UserTable.css';

const UserTable = ({ users, onRowClick, onSort, sortConfig }) => {
  const [columnWidths, setColumnWidths] = useState({});

  const thRefs = useRef([]);

  useEffect(() => {
    const initialWidths = {};
    thRefs.current.forEach((th, index) => {
      initialWidths[index] = th ? th.offsetWidth : 0;
    });
    setColumnWidths(initialWidths);
  }, []);

  const handleResize = (index) => (event) => {
    const newWidth = event.target.offsetWidth;
    setColumnWidths((prevWidths) => ({
      ...prevWidths,
      [index]: newWidth < 50 ? 50 : newWidth, // Минимальная ширина 50px
    }));
  };

  const getSortIndicator = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? '↑' : '↓';
    }
    return '↕';
  };

  return (
    <table className="table">
      <thead>
        <tr>
          {['fullName', 'age', 'gender', 'phone', 'address'].map((key, index) => (
            <th
              key={key}
              ref={(el) => (thRefs.current[index] = el)}
              style={{ width: columnWidths[index] }}
              onClick={() => onSort(key)}
              onMouseUp={handleResize(index)}
            >
              {key === 'fullName' && <>ФИО {getSortIndicator(key)}</>}
              {key === 'age' && <>Возраст {getSortIndicator(key)}</>}
              {key === 'gender' && <>Пол {getSortIndicator(key)}</>}
              {key === 'phone' && <>Номер телефона</>}
              {key === 'address' && <>Адрес {getSortIndicator(key)}</>}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {users.map((user) => (
          <tr key={user.id} onClick={() => onRowClick(user)}>
            <td>{user.firstName} {user.lastName}</td>
            <td>{user.age}</td>
            <td>{user.gender}</td>
            <td>{user.phone}</td>
            <td>{user.address.city}, {user.address.address}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
