import React from 'react';

const UserTable = ({ users, onRowClick, onSort, sortConfig }) => {
  const getClassNamesFor = (name) => {
    if (!sortConfig) return;
    return sortConfig.key === name ? sortConfig.direction : undefined;
  };

  return (
    <table style={{ width: '100%', maxWidth: '1200px', borderCollapse: 'collapse', margin: '20px 0' }}>
      <thead>
        <tr>
          <th onClick={() => onSort('fullName')} className={getClassNamesFor('fullName')}>ФИО</th>
          <th onClick={() => onSort('age')} className={getClassNamesFor('age')}>Возраст</th>
          <th onClick={() => onSort('gender')} className={getClassNamesFor('gender')}>Пол</th>
          <th onClick={() => onSort('phone')} className={getClassNamesFor('phone')}>Номер телефона</th>
          <th onClick={() => onSort('address')} className={getClassNamesFor('address')}>Адрес</th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr key={user.id} onClick={() => onRowClick(user)} style={{ cursor: 'pointer', borderBottom: '1px solid #ddd' }}>
            <td>{`${user.firstName} ${user.lastName}`}</td>
            <td>{user.age}</td>
            <td>{user.gender}</td>
            <td>{user.phone}</td>
            <td>{`${user.address.city}, ${user.address.address}`}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
