import React from 'react';

const UserTable = ({ users, onRowClick, onSort, sortConfig }) => {
  const getSortDirection = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? '↓' : '↑';
    }
    return '↕';
  };

  return (
    <table style={{ width: '100%', maxWidth: '1200px', margin: '0 auto' }}>
      <thead>
        <tr>
          <th onClick={() => onSort('firstName')}>ФИО {getSortDirection('firstName')}</th>
          <th onClick={() => onSort('age')}>Возраст {getSortDirection('age')}</th>
          <th onClick={() => onSort('gender')}>Пол {getSortDirection('gender')}</th>
          <th>Номер телефона</th>
          <th onClick={() => onSort('address')}>Адрес {getSortDirection('address')}</th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr key={user.id} onClick={() => onRowClick(user)}>
            <td>{user.firstName} {user.lastName}</td>
            <td>{user.age}</td>
            <td>{user.gender}</td>
            <td>{user.phone}</td>
            <td>{user.address.city}, {user.address.street}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
