import React, { useState } from 'react';

const UserTable = ({ users, onRowClick }) => {
  const [sortConfig, setSortConfig] = useState({ key: null, direction: null });

  const sortUsers = (users) => {
    if (sortConfig.key) {
      return [...users].sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }
    return users;
  };

  const requestSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    } else if (sortConfig.key === key && sortConfig.direction === 'descending') {
      direction = null;
      key = null;
    }
    setSortConfig({ key, direction });
  };

  const sortedUsers = sortUsers(users);

  return (
    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
      <thead>
        <tr>
          <th onClick={() => requestSort('name')}>ФИО</th>
          <th onClick={() => requestSort('age')}>Возраст</th>
          <th onClick={() => requestSort('gender')}>Пол</th>
          <th>Номер телефона</th>
          <th onClick={() => requestSort('address.city')}>Адрес (город и улица)</th>
        </tr>
      </thead>
      <tbody>
        {sortedUsers.map(user => (
          <tr key={user.id} onClick={() => onRowClick(user)}>
            <td>{user.name}</td>
            <td>{user.age}</td>
            <td>{user.gender}</td>
            <td>{user.phone}</td>
            <td>{`${user.address.city}, ${user.address.street}`}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default UserTable;
